package com.spring.app.listautowired;import org.springframework.beans.factory.InitializingBean;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Configuration;import org.springframework.core.Ordered;import org.springframework.core.annotation.Order;import java.util.Collections;import java.util.List;import java.util.Map;/** * @author shenxie * @date 2020/9/10 */@Configurationpublic class UseDemoInterfaceImpl implements InitializingBean , Ordered {    /**     * 自动注入List泛型中的接口的所有的实现类     */    @Autowired    private List<DemoInterface> demoInterfaces = Collections.emptyList();    /**     * k : 默认为在spring中注入的id  ， v : 该类在堆中的地址：     * 举例：     * k:demoInterfaceV1 ,  v: com.spring.app.listautowired.DemoInterfaceV1@352c308     * k:demoInterfaceV2 ,  v: com.spring.app.listautowired.DemoInterfaceV2@7d373bcf     */    @Autowired    private Map<String, DemoInterface> demoInterfaceMap;    /**     * 使用List完成Spring的自动注入     */    public void showInfo(){        System.out.println("使用List实现Spring的自动注入===============开始执行");        demoInterfaces.forEach(demoInterface -> {            System.out.println(demoInterface.showInfo());        });        demoInterfaceMap.forEach((k , v) -> System.out.println(String.format("k:%s ,  v: %s" , k , v)));        System.out.println("使用List实现Spring的自动注入===============结束执行");    }    @Override    public void afterPropertiesSet() throws Exception {        showInfo();    }    @Override    public int getOrder() {        return -1000;    }}